import "./App.css";
import {useState} from "react";
import Axios from "axios";
import Dropdown from 'react-dropdown';

function App() {
    const [name, setName] = useState("");
    const [age, setAge] = useState(0);
    const [country, setCountry] = useState("");
    const [position, setPosition] = useState("");
    const [wage, setWage] = useState(0);
    const [newWage, setNewWage] = useState(0);
    const [newAge, setNewAge] = useState(0);
    const [employeeList, setEmployeeList] = useState([]);
    const [bestelList, setBestelLust] = useState([]);

    const options = [{value: 'Wage', label:'Wage'}, {value: 'Age', label:'Age'}];

    const addEmployee = () => {
        Axios.post("http://localhost:3001/create", {
            name: name,
            age: age,
            country: country,
            position: position,
            wage: wage,
        }).then(() => {
            setEmployeeList([
                ...employeeList,
                {
                    name: name,
                    age: age,
                    country: country,
                    position: position,
                    wage: wage,
                },
            ]);
        });
    };
    const getEmployees = () => {
        Axios.get("http://localhost:3001/employees").then((response) => {
            setEmployeeList(response.data);
        });
    };
    const updateEmployeeWage = (id) => {
        Axios.put("http://localhost:3001/update", {wage: newWage, id: id}).then(
            (response) => {
                setEmployeeList(
                    employeeList.map((val) => {
                        return val.id === id
                            ? {
                                id: val.id,
                                name: val.name,
                                country: val.country,
                                age: val.age,
                                position: val.position,
                                wage: newWage,
                            }
                            : val;
                    })
                );
            }
        );
    };

    const updateEmployeeAge = (id) => {
        Axios.put("http://localhost:3001/updateAge", {age: newAge, id: id}).then(
            (response) => {
                setEmployeeList(
                    employeeList.map((val) => {
                        return val.id === id
                            ? {
                                id: val.id,
                                name: val.name,
                                country: val.country,
                                age: newAge,
                                position: val.position,
                                wage: val.wage,
                            }
                            : val;
                    })
                );
            }
        );
    };

    const deleteEmployee = (id) => {
        Axios.delete(`http://localhost:3001/delete/${id}`).then((response) => {
            setEmployeeList(
                employeeList.filter((val) => {
                    return val.id !== id;
                })
            );
        });
    };

    const productenView = () => {
        Axios.get('http://localhost:3001/moonen').then((response) => {
            setBestelLust(response.data);
        })
    }

    return (
        <div className="App">
            <div className="information">
                <label>Name:</label>
                <input
                    type="text"
                    onChange={(event) => {
                        setName(event.target.value);
                    }}
                />
                <label>Age:</label>
                <input
                    type="number"
                    onChange={(event) => {
                        setAge(event.target.value);
                    }}
                />
                <label>Country:</label>
                <input
                    type="text"
                    onChange={(event) => {
                        setCountry(event.target.value);
                    }}
                />
                <label>Position:</label>
                <input
                    type="text"
                    onChange={(event) => {
                        setPosition(event.target.value);
                    }}
                />
                <label>Wage (year):</label>
                <input
                    type="number"
                    onChange={(event) => {
                        setWage(event.target.value);
                    }}
                />
                <button onClick={addEmployee}>Add Employee</button>
            </div>

            <div className="employees">
                <button onClick={getEmployees}>Show Employees</button>
                {employeeList.map((val, key) => {
                    return (
                        <div className="employee">
                            <div>
                                <h3>Name: {val.name}</h3>
                                <h3>Age: {val.age}</h3>
                                <h3>Country: {val.country}</h3>
                                <h3>Position: {val.position}</h3>
                                <h3>Wage: {val.wage}</h3>
                            </div>
                            <div>
                                <Dropdown options={options} value={options[0]} placeholder="Select an option" />
                                <input
                                    type="text"
                                    placeholder="2000..."
                                    onChange={(event) => {
                                        if (options === 'Age'){
                                            console.log(options)
                                            setNewAge(event.target.value);
                                        } else if ( options === 'Wage') {
                                            console.log(options)
                                            setNewWage(event.target.value);
                                        }
                                    }}
                                />
                                <button
                                    onClick={() => {
                                        updateEmployeeWage(val.id);
                                    }}
                                >
                                    {" "}
                                    Update
                                </button>
                                <button
                                    onClick={() => {
                                        deleteEmployee(val.id);
                                    }}
                                >
                                    Delete
                                </button>
                                <button onClick={() => {
                                    updateEmployeeAge(val.id);
                                }}>
                                    Update Age
                                </button>
                            </div>
                        </div>
                    );
                })}
            </div>

            <div className="producten">
                <button onClick={productenView}>Show producten</button>
                {bestelList.map((val, key) => {
                    return (
                        <div className="product">
                            <div>
                                <h3>Name: {val.Naam}</h3>
                                <h3>Artikkelnummer: {val.Artikelnummer}</h3>
                                <h3>Minimale Voorraad: {val.MinimaleVooraad}</h3>
                                <h3>Actuele Voorraad: {val.ActueleVoorraad}</h3>
                            </div>
                        </div>
                    );
                })}
            </div>
        </div>


    );
}

export default App;
