import React, {useState} from "react";
import Axios from "axios";

class BestelLijstView extends React.Component {
    render() {
        return (
            <div>
                <h1>Test</h1>
                <Bestelijst />
            </div>
        )
    }
}

function Bestelijst() {
    const [bestellijst, setBestelijst] = useState([])

    const getProducten = () => {
        Axios.get('http://localhost:3001/moonen').then((response) => {
            setBestelijst(response.data);
        })
    };

    const bestelAdvies = (minimaal, huidig) => {
        if (minimaal - huidig > 0) {
            return minimaal - huidig;
        } else {
            return 0;
        }
    };

    return (
        <div className="test">
            <button onClick={getProducten}>laat de bestellijst zien</button>
            {bestellijst.map((val) => {
                if (bestelAdvies(val.MinimaleVooraad - val.ActueleVoorraad) > 0) {
                    return (
                        <div>
                            <p>Naam: {val.Naam}</p>
                            <p>Artikkelnummer: {val.Artikelnummer}</p>
                            <p>BestelAdvies: {bestelAdvies(val.MinimaleVooraad - val.ActueleVoorraad)}</p>
                        </div>
                    )
                }
            })}
        </div>
    )
}

export default Bestelijst
