import React, {Component, useState} from 'react';
import fire from './fire'

class TestClass extends Component {
    constructor(props) {
        super(props);
        this.state = {producten: []}
    }

    componentDidMount() {
        let productRef = fire.database().ref('Bakpapier').limitToLast(100);
        productRef.on('child_added', snapshot => {
            let product = {Naam: snapshot.val(), id: snapshot.key()};
            this.setState({producten: [product].concat(this.state.producten)
            });
        })
    }

    addProduct(e){
        e.preventDefault();
        fire.database().ref('Bakpapier').push(this.inputEl.value());
        this.inpuEl.value = ''
    }

    render() {
        return(
            <form onSubmit={this.addProduct.bind(this)}>
                <input type="text" ref={ el => this.inputEl = el}/>
                <input type="submit"/>

                <ul>
                    {
                        this.state.producten.map( product => <li key={product.id}>
                            {product.text}
                        </li> )
                    }
                </ul>

            </form>
        )
    }

}

export default TestClass;
