import React, {useState} from "react";
import Axios from "axios";

function Bestelijst() {
    const [bestellijst, setBestelijst] = useState([])

    const getProducten = () => {
        Axios.get('http://localhost:3001/getData').then((response) => {
            setBestelijst(response.data);
        })
    };

    const bestelAdvies = (minimaal, huidig) => {
        if (minimaal - huidig > 0) {
            return minimaal - huidig;
        } else {
            return 0;
        }
    };

    return (
        <table>
            <td>Naam</td>
            <td>ArtikkelNummer</td>
            <td>bestelAdvies</td>

            <div className="test">
                <button onClick={getProducten}>laat de bestellijst zien</button>
                {bestellijst.map((val, key) => {
                    if (bestelAdvies(val.MinimaleVooraad, val.ActueleVoorraad) > 0) {
                        return <p key={val.Artikelnummer}>naam: {val.Naam} artikkelNummer: {val.Artikelnummer} bestel
                            advies: {bestelAdvies(val.MinimaleVooraad, val.ActueleVoorraad)}</p>
                    }
                })}
            </div>
        </table>
    )
}

export default Bestelijst
