import "./App.css";
import React, { useState as State } from 'react';
import Axios from "axios";

function productenView() {
    const [bestelList, setBestelLust] = State([]);
    const [voorraad, newVoorraad] = State(0);

    const productenView = () => {
        Axios.get('http://localhost:3001/moonen').then((response) => {
            setBestelLust(response.data);
        })

    }

    const updateVoorraad = (id) => {
        Axios.put('http://localhost:3001/moonen', {voorraad: newVoorraad, id: id}).then((response) => {
            setBestelLust(
                bestelList.map((val) => {
                    return val.id === id ? {
                            naam: val.naam,
                            artikkelNummer: val.artikkelNummer,
                            minimaleVoorraad: val.minimaleVoorraad,
                            actueleVoorraad: newVoorraad,
                        }
                        : val;
                })
            );
        });
    };

    return (
        <div className="productenView">
            <button onClick={productenView}>Show producten</button>
            {bestelList.map((val, key) => {
                return (
                    <div className="product">
                        <h3>Naam: {val.naam}</h3>
                        <h3>Artikkelnummer: {val.artikkelNummer}</h3>
                        <h3>Minimale voorraad: {val.minimaaleVoorraad}</h3>
                        <h3>Actuele Voorraad: {val.actueleVoorraad}</h3>
                    </div>
                );
            })}
        </div>
    );
}

export default productenView();
